mapper.update:
UPDATE product
prod_code = #{prodCode}, prod_name = #{prodName}, prod_detail = #{prodDetail}, due_date = #{dueDate}, cost = #{cost}, price = #{price}, image_file = #{fileName : VARCHAR}
WHERE prod_no = #{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT * FROM product
prod_code = #{searchCondition}
prod_name LIKE '%' || #{searchKeyword} || '%'
prod_no LIKE '%' || #{searchKeyword} || '%'
ORDER BY prod_no DESC) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*) FROM ( SELECT * FROM product
prod_code = #{searchCondition}
prod_name LIKE '%' || #{searchKeyword} || '%'
prod_no LIKE '%' || #{searchKeyword} || '%'
) countTable
