mapper.update:
UPDATE transaction
payment_option = #{paymentOption}, receiver_name = #{receiverName}, receiver_phone = #{receiverPhone}, demailaddr = #{divyAddr}, dlvy_message = #{divyMessage}
WHERE tran_no = #{tranNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT t.tran_no, t.prod_no, t.buyer_id, t.tran_status_code, t.order_date, t.dlvy_date, t.dlvy_message, p.seller_id, p.prod_name FROM transaction t, product p
t.buyer_id = #{searchKeyword} AND t.prod_no = p.prod_no
ORDER BY t.order_date DESC) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT t.tran_no, t.prod_no, t.buyer_id, t.tran_status_code, t.order_date, t.dlvy_date, p.seller_id, p.prod_name FROM transaction t, product p
t.prod_no = p.prod_no AND p.seller_id = #{searchKeyword}
ORDER BY order_date DESC) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
